<?phpdefined('BASEPATH') or exit('No direct script access allowed');class Web extends CI_Controller{    function __construct()    {        parent::__construct();        $this->load->model(['Web_m']);        $this->load->library('pagination');        if (!$this->session->userdata('site_lang')) {            $this->session->set_userdata('site_lang', 'english');            $this->lang->load('content', 'english');        } else {            $this->lang->load('content', $this->session->userdata('site_lang'));        }        $this->company_data = $this->fungsi->company_data();      /*  $this->jobs_tasnif = $this->Web_m->get_jobs_tasnif();        $this->product_tasnif = $this->Web_m->get_product_tasnif();        $this->about_us = $this->Web_m->get_about_us();*/    }    public function messages($type, $text, $method = false)    {        $CI =& get_instance();        $CI->load->library("session");        if ($type == 'success') {            return $CI->session->set_flashdata('message', '<script  src="'.base_url() . 'assets/sweet_alert2/sweetalert2.js"></script><script  src="'.base_url() . 'assets/sweet_alert2/sweetalert2.all.js"></script><script  src="'. base_url() . 'assets/sweet_alert2/sweetalert2.all.min.js"></script><script> Swal.fire({                    icon:"success",                    title:"' . $text . '"                 })</script>');        } elseif ($type == 'warning') {            return $CI->session->set_flashdata('message', '<div class="alert alert-warning alert-dismissable"><a href="#" class="close" data-dismiss="alert" aria-label="close">&times;</a><strong>   !</strong> ' . $text . '.</div>');        } elseif ($type == 'error') {            return $CI->session->set_flashdata('message', '<div class="alert alert-danger alert-dismissable"><a href="#" class="close" data-dismiss="alert" aria-label="close">&times;</a><strong> !</strong> ' . $text . '.</div>');        }    }    public function index()    {        $data['title'] = 'الرئيسية';        $data['subview'] = 'web_v/home';        $this->load->view('template_web', $data);    }}