<?phpclass News_m extends CI_Model{    var $column_order_groups = array(null, 'tbl_news.image', 'tbl_news.name_ar', 'tbl_news.name_en', 'tbl_news.link'); //set column field database for datatable orderable    var $column_search_groups = array('tbl_news.image', 'tbl_news.name_ar', 'tbl_news.name_en', 'tbl_news.link'); //set column field database for datatable searchable    var $order_groups = array('tbl_news.id' => 'desc');    private function _get_datatables_query()    {        $this->db->select('tbl_news.*');        $this->db->from('tbl_news ');        $i = 0;        foreach ($this->column_search_groups as $item) {            if (@$_POST['search']['value']) {                if ($i === 0) { // first loop                    $this->db->group_start();                    $this->db->like($item, $_POST['search']['value']);                } else {                    $this->db->or_like($item, $_POST['search']['value']);                }                if (count($this->column_search_groups) - 1 == $i)                    $this->db->group_end();            }            $i++;        }        if (isset($_POST['order'])) {            $this->db->order_by($this->column_order_groups [$_POST['order']['0']['column']], $_POST['order']['0']['dir']);        } else if (isset($this->order_groups)) {            $order = $this->order_groups;            $this->db->order_by(key($order), $order[key($order)]);        }    }    function get_datatables()    {        $this->_get_datatables_query();        if (@$_POST['length'] != -1)            $this->db->limit(@$_POST['length'], @$_POST['start']);        $query = $this->db->get();        return $query->result();    }    function count_filtered()    {        $this->_get_datatables_query();        $query = $this->db->get();        return $query->num_rows();    }    function count_all()    {        $this->db->select('tbl_news.*');        $this->db->from('tbl_news ');        return $this->db->count_all_results();    }    /*************************************************************************/    public function get($id = null)    {        $this->db->select('tbl_news.* , users.name as publisher_name ');        $this->db->from('tbl_news');        $this->db->join('users', 'users.user_id = tbl_news.user_id');        if ($id != null) {            $this->db->where('id', $id);        }        $this->db->order_by('id', 'desc');        $query = $this->db->get();        return $query;    }    public function add($post, $image = '', $images = array())    {        $params = [            'user_id' => $this->session->userdata('userid'),            'image' => $image,            'name_ar' => $post['name_ar'],            'name_en' => $post['name_en'],            'name_ru' => $post['name_ru'],                        'description_ar' => $post['description_ar'],            'description_en' => $post['description_en'],            'description_ru' => $post['description_ru'],            'create_at' => date('Y-m-d H:i:s')        ];        $this->db->insert('tbl_news', $params);        $insert_id = $this->db->insert_id();        if (!empty($images) && (count($images) > 0)) {            $this->db->where('news_id_fk', $post['id'])->delete('tbl_news_images');            foreach ($images as $key => $one_image) {                $image_data['news_id_fk'] = $insert_id;                $image_data['image'] = $one_image;                $this->db->insert('tbl_news_images', $image_data);            }        }    }    public function edit($post, $image = '', $images = array())    {        $params = [            'name_ar' => $post['name_ar'],            'name_en' => $post['name_en'],            'name_ru' => $post['name_ru'],                        'description_ar' => $post['description_ar'],            'description_en' => $post['description_en'],            'description_ru' => $post['description_ru'],            'updated' => date('Y-m-d H:i:s')        ];        if (!empty($image)) {            $params['image'] = $image;        }        $this->db->where('id', $post['id']);        $this->db->update('tbl_news', $params);        if (!empty($images) && (count($images) > 0)) {            $this->db->where('news_id_fk', $post['id'])->delete('tbl_news_images');            foreach ($images as $key => $one_image) {                $image_data['news_id_fk'] = $post['id'];                $image_data['image'] = $one_image;                $this->db->insert('tbl_news_images', $image_data);            }        }    }    public function delete($id)    {        $item = $this->get($id)->row();        if ($item->image != null) {            $target_file = './uploads/news/' . $item->image;            unlink($target_file);        }        $this->db->where('id', $id);        $this->db->delete('tbl_news');    }}?>